import React, { useCallback, useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";

import { getAllAdminAsync } from "../../slices/admin.js";
import { Table } from "../../components/Table.js";
import { Text } from "../../components/Text.js";
import deleteIcon from "../../assets/icons/delete.svg";
import activate from "../../assets/icons/activate.svg";
import deactivate from "../../assets/icons/deactivate.svg";
import edit from "../../assets/icons/edit.svg";
import { ModalPopup } from "../../components/ModalPopup.js";
import { DeleteAdmin } from "./DeleteAdmin.js";
import { ActivateAdmin } from "./ActivateAdmin.js";
import { EditAdmin } from "./EditAdmin.js";
import { DeactivateAdmin } from "./DeactivateAdmin.js";
import { useOutletContext } from "react-router-dom";
import MobileAdminTable from "./MobileAdminTable.js";
import MobileAdminSinglePage from "./MobileAdminSinglePage.js";
import { Loader } from "../../atoms/Loader.js";

const Admin = () => {
  const { setPageHeader, setIfButton, setSearchBar } = useOutletContext();
  useEffect(() => {
    setPageHeader(`Admin`);
    setIfButton(true);
    setSearchBar(true);
  }, []);

  const dispatch = useDispatch();
  const { getAllAdminsLoading } = useSelector((state) => state.admin);

  const [adminData, setAdminData] = useState([]);
  const [deleteAdmin, setDeleteAdmin] = useState(false);
  const [activateAdmin, setActivateAdmin] = useState(false);
  const [singleAdmin, setSingleAdmin] = useState(false);
  const [editAdmin, setEditAdmin] = useState(false);
  const [deactivateAdmin, setDeactivateAdmin] = useState(false);
  const [currentView, setCurrentView] = useState("default");
  const [totalNumberOfPages, setTotalNumberOfPages] = useState(10);

  const toggleDeleteAdmin = (item) => {
    setDeleteAdmin(!deleteAdmin);
    setSingleAdmin(item);
  };

  const toggleActivateAdmin = (item) => {
    setActivateAdmin(!activateAdmin);
    setSingleAdmin(item);
  };

  const toggleEditAdmin = (item) => {
    setEditAdmin(!editAdmin);
    setSingleAdmin(item);
  };

  const toggleDeactivateAdmin = (item) => {
    setDeactivateAdmin(!deactivateAdmin);
    setSingleAdmin(item);
  };

  const getAdminData = async () => {
    const data = await dispatch(getAllAdminAsync());
    setAdminData(data?.payload?.data?.admins);
  };

  const getNextPage = () => {
    console.log("test");
  };

  useEffect(() => {
    getAdminData();
  }, []);

  const getData = useCallback(() => {
    const result =
      adminData &&
      adminData?.map((item, i) => {
        return {
          sn: <Text type="body2">{i + 1}</Text>,
          name: <Text type="body2">{item?.name}</Text>,
          emailAddress: <Text type="body2">{item?.email}</Text>,
          status: (
            <p
              className={`text-sm w-max py-1 px-4 border rounded-xl ${
                item?.active === 1
                  ? "bg-greenOne text-greenTwo"
                  : "bg-redOne text-redTwo"
              }`}
            >
              <Text
                className={`font-semibold text-xs  ${
                  item?.active === 1 ? " text-greenTwo" : " text-redTwo"
                }`}
              >
                {item?.active === 1 ? "Active" : "Inactive"}
              </Text>
            </p>
          ),
          role: <Text type="body2">{item?.role}</Text>,
          action: (
            <div className="flex items-center">
              <div
                className="tooltip cursor-pointer"
                onClick={() => toggleEditAdmin(item)}
              >
                <span className="tooltiptext">Edit</span>
                <img src={edit} alt="edit" className="w-[43px]" />
              </div>

              {item?.active === 0 && (
                <div
                  className="tooltip cursor-pointer"
                  onClick={() => toggleActivateAdmin(item)}
                >
                  <span className="tooltiptext">Activate</span>
                  <img src={activate} alt="activate" className="w-[43px]" />
                </div>
              )}

              {item?.active === 1 && (
                <div
                  className="tooltip cursor-pointer"
                  onClick={() => toggleDeactivateAdmin(item)}
                >
                  <span className="tooltiptext">Deactivate</span>
                  <img src={deactivate} alt="deactivate" className="w-[43px]" />
                </div>
              )}

              <div
                className="tooltip cursor-pointer"
                onClick={() => toggleDeleteAdmin(item)}
              >
                <span className="tooltiptext">Delete</span>
                <img src={deleteIcon} alt="deleteIcon" className="w-[43px]" />
              </div>
            </div>
          ),
        };
      });
    return [...(result || [])];
  }, [adminData]);

  const switchView = (view = "default", index = 0) => {
    let results = {
      default() {
        setCurrentView("default");
      },
      single() {
        setCurrentView("single");
        setSingleAdmin(adminData[index]);
      },
    };
    results[view]();
  };

  const columns = React.useMemo(
    () => [
      {
        Header: "S/N",
        accessor: "sn",
      },
      {
        Header: "Name",
        accessor: "name",
      },
      {
        Header: "Email Address",
        accessor: "emailAddress",
      },
      {
        Header: "Status",
        accessor: "status",
      },
      {
        Header: "Role",
        accessor: "role",
      },
      {
        Header: "Action",
        accessor: "action",
      },
    ],
    []
  );

  const data = React.useMemo(() => getData(), [getData]);

  return (
    <div>
      {currentView === "default" ? (
        <>
          <div className="hidden md:block">
            <Table
              data={data}
              columns={columns}
              isLoading={getAllAdminsLoading}
              fetchData={getNextPage}
              pageCount={totalNumberOfPages}
            />
          </div>
          <div className="block md:hidden mt-10">
            {getAllAdminsLoading ? (
              <Loader />
            ) : (
              <MobileAdminTable
                singleAdmin={singleAdmin}
                data={data}
                switchView={switchView}
              />
            )}
          </div>
        </>
      ) : (
        <div className="">
          {/* <p>{item}</p> */}
          <MobileAdminSinglePage
            singleAdmin={singleAdmin}
            switchView={switchView}
          />
        </div>
      )}

      <ModalPopup
        isOpen={deleteAdmin}
        children={
          <DeleteAdmin
            closeDetailsModal={toggleDeleteAdmin}
            singleAdmin={singleAdmin}
          />
        }
      />

      <ModalPopup
        isOpen={activateAdmin}
        children={
          <ActivateAdmin
            closeDetailsModal={toggleActivateAdmin}
            singleAdmin={singleAdmin}
          />
        }
      />

      <ModalPopup
        isOpen={editAdmin}
        modalHeight="500px"
        children={
          <EditAdmin
            closeDetailsModal={toggleEditAdmin}
            singleAdmin={singleAdmin}
          />
        }
      />

      <ModalPopup
        isOpen={deactivateAdmin}
        children={
          <DeactivateAdmin
            closeDetailsModal={toggleDeactivateAdmin}
            singleAdmin={singleAdmin}
          />
        }
      />
    </div>
  );
};

export { Admin };
