import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

import { NavLink } from "react-router-dom";
import { Text } from "../components";
import sideArrowUp from "../assets/icons/sideArrowUp.svg";
import sideArrowDown from "../assets/icons/sideArrowDown.svg";

const SubMenu = (props) => {
  const navigate = useNavigate();

  const [dropDown, setDropDown] = useState(false);
  const [activeTab, setActiveTab] = useState("Data Consumers");

  const toggleDropDown = () => {
    setDropDown(!dropDown);
  };

  const goToPath = (item) => {
    navigate(item?.path, { replace: true });
  };

  const changeActive = (item) => {
    setActiveTab(item?.item);
    // console.log(activeTab);
  };

  return (
    <div>
      {props?.item?.subMenu ? (
        <>
          {props?.item?.subMenu ? (
            <div
              className="flex items-center mt-6 hover:text-lighterBlue"
              key={props?.item?.id}
            >
              <props.item.Image
                className={`mr-4  ${
                  props?.item?.useStroke ? "stroke-current" : "fill-current"
                }`}
              />
              {props?.item?.menuItem}
              <p className="font-normal text-base text-black/60"></p>
              {/* <p className="font-normal text-base text-black/60"></p> */}
              <Text type="body3" className="mr-4"></Text>
              {dropDown ? (
                <img
                  src={sideArrowUp}
                  alt="arrowDown"
                  className="pr-9 cursor-pointer"
                  onClick={toggleDropDown}
                />
              ) : (
                <img
                  src={sideArrowDown}
                  alt="arrowDown"
                  className="pr-9 cursor-pointer"
                  onClick={toggleDropDown}
                />
              )}
            </div>
          ) : null}
        </>
      ) : (
        <NavLink
          to={props?.item?.path}
          onClick={() => props.setSidebar(false)}
          className={({ isActive }) =>
            isActive && window.location.pathname === props?.item?.path
              ? "text-darkBlue flex items-center mt-6 2xl:text-lg xl:text-sm"
              : "flex items-center mt-6 hover:text-lighterBlue 2xl:text-lg xl:text-sm"
          }
          key={props?.item?.id}
        >
          <props.item.Image
            className={`mr-4  ${
              props?.item?.useStroke ? "stroke-current" : "fill-current"
            }`}
          />
          <Text type="body3">{props?.item?.menuItem}</Text>
        </NavLink>
      )}

      <div className="mt-4">
        {dropDown &&
          props?.item?.subMenu.map((item) => {
            return (
              <div className="py-4">
                <div
                  onClick={() => goToPath(item)}
                  key={item?.id}
                  className="cursor-pointer"
                >
                  <div className="">
                    <p
                      onClick={() => changeActive(item)}
                      className={`font-normal text-base text-black/50 ${
                        activeTab === item?.item ? "text-darkBlue" : ""
                      }`}
                    >
                      {item?.item}
                    </p>
                  </div>
                </div>
              </div>
            );
          })}
      </div>
    </div>
  );
};

export { SubMenu };
