import React from "react";

const Button = ({
  buttonText,
  disabled,
  isLoading,
  type,
  className,
  size,
  onClick,
}) => {
  return (
    <button
      onClick={onClick}
      disabled={disabled}
      className={`${
        type === "primary" ? "bg-primary text-white" : "bg-primary text-white"
      }px-4 py-4 rounded-[30px] ${className}`}
      style={{
        width:
          size === "lg"
            ? "100%"
            : size === "md"
            ? "50%"
            : size === "sm"
            ? "40%"
            : "100%",
      }}
    >
      <p className="text-xl font-semibold text-white">{buttonText}</p>
    </button>
  );
};

export { Button };
