import React from "react";

// import info from "../assets/icons/moreInfo.svg";
import { Text } from "../components";

const CustomerCard = ({ title, totalAmount, ifNaira = true }) => {
  return (
    <section className="min-w-[260px] md:min-w-0 lg:pb-0 2xl:pb-4 bg-white rounded-md w-[23%] mr-6 2xl:mr-8 lg:h-28 pl-5 lg:pl-4 2xl:pl-6">
      <section className="flex flex-col items-start justify-center h-full">
        <div className="flex items-center pt-5 2xl:pt-6 lg:h-10">
          <Text type="body2" className="pr-2 2xl:pr-4 ">
            {title}
          </Text>
        </div>

        <Text type="h7" className="pb-5 md:pb-0 2xl:pt-4">
          {ifNaira && "₦"}
          {totalAmount}
        </Text>
      </section>
    </section>
  );
};

export { CustomerCard };
