import React, { useState } from "react";
// import { useNavigate } from "react-router-dom";
// import { NavLink } from "react-router-dom";

import { SidebarMenuItems } from "../data/Sidebar";
import { LogoutModal } from "./LogoutModal";
import oystrLogo from "../assets/images/logo.png";
import { SubMenu } from "./SubMenu";
import logout from "../assets/icons/logout.svg";
import { Text, ModalPopup } from "../components";

const Sidebar = (props) => {
  // const navigate = useNavigate();

  // const [dropDown, setDropDown] = useState(false);
  const [logoutModal, setLogoutModal] = useState(false);

  const toggleLogoutModal = () => {
    // navigate("/logout", { replace: true });
    setLogoutModal(!logoutModal);
  };
  // const toggleDropDown = () => {
  //   setDropDown(!dropDown);
  // };
  //   const [openLogoutModal, setOpenLogoutModal] = useState(false);
  //   const closeLogoutModal = () => {
  //     setOpenLogoutModal(false);
  //   };

  return (
    <div
      className={`${props.className} px-8 lg:px-4 xl:px-6 2xl:px-12 flex flex-col overflow-auto`}
    >
      <div className="my-8">
        <img
          src={oystrLogo}
          alt="oystrLogo"
          className="w-24 md:w-28 2xl:w-32"
        />
      </div>
      {/* id, menuItem, Image, path, useStroke */}
      <div className="mb-8 pb-12">
        {SidebarMenuItems.map((item, index) => {
          return <SubMenu item={item} key={index} props={props} />;
        })}

        <div
          to="/"
          className="flex items-center mt-6 cursor-pointer 2xl:text-lg xl:text-sm"
          key="logout"
          onClick={toggleLogoutModal}
        >
          <img src={logout} alt="menuLogo" className="mr-2" />
          <Text>Logout</Text>
        </div>
      </div>

      <ModalPopup
        modalHeight="400px"
        children={<LogoutModal closeDetailsModal={toggleLogoutModal} />}
        isOpen={logoutModal}
      />
    </div>
  );
};

export { Sidebar };
