import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import * as Yup from "yup";
import { useNavigate } from "react-router-dom";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";

import eyeOpen from "../assets/icons/eyeOpen.svg";
import passwordIcon from "../assets/icons/passwordIcon.svg";
import { loginAsync } from "../slices/auth.js";
import { Button } from "../components/Button.js";
import { AuthLayout } from "../Layout/index.js";
import { Input } from "../components/Input.js";
import { toast } from "react-toastify";

const SignIn = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const { isLoading } = useSelector((state) => state.auth);

  const [passwordShown, setPasswordShown] = useState(false);

  const validationSchema = Yup.object().shape({
    email: Yup.string()
      .email("Invalid email format")
      .required("Email is Required"),
    password: Yup.string().required("Password is required"),
  });

  const formOptions = { resolver: yupResolver(validationSchema) };

  const { register, handleSubmit, formState } = useForm({
    formOptions,
    // mode: "onTouched",
  });

  const { errors } = formState;

  const togglePasswordVisibility = () => {
    setPasswordShown(passwordShown ? false : true);
  };

  const submitForm = async (data) => {
    await dispatch(loginAsync(data))
      .unwrap()
      .then((res) => {
        if (res?.status && res?.message === "Login Successful") {
          localStorage.setItem("accessToken", res?.data?.access_token);
          navigate("/dashboard", { replace: true });
        }
      })
      .catch((err) => {
        console.log(err);
        toast.error(err?.message);
      });
  };

  return (
    <>
      <AuthLayout
        ifText={true}
        title="Alternative financial data infrastructure
        for Africa"
        textTitle="Admin"
        subText="Please Fill in Your Details"
      >
        <form
          className="w-full md:w-9/12 m-auto py-3"
          onSubmit={handleSubmit(submitForm)}
        >
          <Input
            label="Email Address"
            type="email"
            register={register("email")}
            error={errors?.email?.message}
          />

          <div className="w-full relative">
            {
              <i onClick={togglePasswordVisibility}>
                <img
                  src={passwordShown ? eyeOpen : passwordIcon}
                  alt="visible"
                  className="w-5 absolute mt-[35px] md:mt-10 ml-[89%]"
                />
              </i>
            }
            <Input
              label="Password"
              type={passwordShown ? "text" : "password"}
              register={register("password")}
              error={errors?.password?.message}
            />
          </div>

          <div className="cursor-pointer my-16">
            <Button
              size="lg"
              buttonText="Sign in"
              className="mt-4"
              isLoading={isLoading}
              disabled={isLoading}
            />
          </div>
        </form>
      </AuthLayout>
    </>
  );
};

export { SignIn };
