import React from "react";

import close from "../../assets/icons/close.svg";
import { Button, Input, SearchableSelect, Text } from "../../components";

const EditRole = ({ closeDetailsModal }) => {
  const options = [
    {
      label: "Super Admin",
      value: "Super Admin",
    },
    {
      label: "Finance",
      value: "Finance",
    },
    {
      label: "Operations",
      value: "Operations",
    },
    {
      label: "Developers",
      value: "Developers",
    },
  ];

  return (
    <div className="px-10">
      <div className="flex justify-end">
        <img
          src={close}
          alt="close"
          className="cursor-pointer mt-8"
          onClick={closeDetailsModal}
        />
      </div>

      <Text type="h6" className="text-center">
        Edit Roles
      </Text>

      <div className="mt-8">
        <Input label="Current Role" />
        <SearchableSelect options={options} />

        <div className="my-8">
          <Button type="primary" buttonText="Save" />
        </div>
      </div>
    </div>
  );
};

export { EditRole };
