import React, { useEffect, useState } from "react";

import { Table } from "../../components/Table.js";
import { Text } from "../../components/Text.js";
import deactivate from "../../assets/icons/deactivate.svg";
import activate from "../../assets/icons/activate.svg";
import loginIcon from "../../assets/icons/login.svg";
import archive from "../../assets/icons/archive.svg";
import { DeactivateCustomer } from "./DeactivateCustomer.js";
import { ModalPopup } from "../../components/ModalPopup.js";
import { ArchiveCustomer } from "./ArchiveCustomer.js";
import { ActivateCustomer } from "./ActivateCustomer.js";
import { Link, useOutletContext } from "react-router-dom";

const OystrSmartCustomers = () => {
  const { setPageHeader } = useOutletContext();
  useEffect(() => {
    setPageHeader(`Oystr Smart`);
  }, []);

  const isActive = true;

  const [deactivateCustomer, setDeactivateCustomer] = useState(false);
  const [activateCustomer, setActivateCustomer] = useState(false);
  const [archiveCustomer, setArchiveCustomer] = useState(false);

  const toggleDeactivateCustomer = () => {
    setDeactivateCustomer(!deactivateCustomer);
  };

  const toggleArchiveCustomer = () => {
    setArchiveCustomer(!archiveCustomer);
  };

  const toggleActivateCustomer = () => {
    setActivateCustomer(!activateCustomer);
  };

  const data = React.useMemo(
    () => [
      {
        sn: <Text type="body2">1</Text>,
        business: (
          <Link to={`/customers/1`}>
            <Text type="body2">Johnson hope Faith</Text>{" "}
          </Link>
        ),
        emailAddress: <Text type="body2">example@email.com</Text>,
        phone: <Text type="body2">+234 8109756478</Text>,
        date: <Text type="body2">23/04/2022</Text>,
        status: (
          <p
            className={`text-sm w-max py-1 px-4 border rounded-xl ${
              isActive === true
                ? "bg-greenOne text-greenTwo"
                : isActive === "pending"
                ? "bg-YELLOW-_100 border-YELLOW-_300 text-yellow-600"
                : "bg-redOne text-#CA0814"
            }`}
          >
            <Text className="font-semibold text-xs">
              {isActive ? "Active" : "Inactive"}
            </Text>
          </p>
        ),
        action: (
          <div className="flex items-center">
            <div
              className="tooltip cursor-pointer"
              // onClick={toggleDeactivateAdmin}
            >
              <span className="tooltiptext">Login</span>
              <img src={loginIcon} alt="loginIcon" />
            </div>

            <div
              className="tooltip cursor-pointer"
              onClick={toggleDeactivateCustomer}
            >
              <span className="tooltiptext">Deactivate</span>
              <img src={deactivate} alt="deactivate" />
            </div>

            <div
              className="tooltip cursor-pointer"
              onClick={toggleArchiveCustomer}
            >
              <span className="tooltiptext">Archive</span>
              <img src={archive} alt="archive" />
            </div>
          </div>
        ),
      },
      {
        sn: <Text type="body2">1</Text>,
        business: <Text type="body2">Johnson hope Faith</Text>,
        emailAddress: <Text type="body2">example@email.com</Text>,
        phone: <Text type="body2">+234 8109756478</Text>,
        date: <Text type="body2">23/04/2022</Text>,
        status: (
          <p
            className={`text-sm w-max py-1 px-4 border rounded-xl ${
              isActive === true
                ? "bg-greenOne text-greenTwo"
                : isActive === "pending"
                ? "bg-YELLOW-_100 border-YELLOW-_300 text-yellow-600"
                : "bg-redOne text-#CA0814"
            }`}
          >
            <Text className="font-semibold text-xs">
              {isActive ? "Active" : "Inactive"}
            </Text>
          </p>
        ),
        action: (
          <div className="flex items-center">
            <div
              className="tooltip cursor-pointer"
              // onClick={toggleDeactivateAdmin}
            >
              <span className="tooltiptext">Login</span>
              <img src={loginIcon} alt="loginIcon" />
            </div>

            <div
              className="tooltip cursor-pointer"
              onClick={toggleActivateCustomer}
            >
              <span className="tooltiptext">Activate</span>
              <img src={activate} alt="deactivate" />
            </div>

            <div
              className="tooltip cursor-pointer"
              onClick={toggleArchiveCustomer}
            >
              <span className="tooltiptext">Archive</span>
              <img src={archive} alt="archive" />
            </div>
          </div>
        ),
      },
    ],
    []
  );

  const columns = React.useMemo(
    () => [
      {
        Header: "S/N",
        accessor: "sn",
      },
      {
        Header: "Name (Business name)",
        accessor: "business",
      },
      {
        Header: "Email Address",
        accessor: "emailAddress",
      },
      {
        Header: "Phone number",
        accessor: "phone",
      },
      {
        Header: "Date onboarded",
        accessor: "date",
      },
      {
        Header: "Status",
        accessor: "status",
      },
      {
        Header: "Action",
        accessor: "action",
      },
    ],
    []
  );

  return (
    <div>
      <Table data={data} columns={columns} />

      <ModalPopup
        isOpen={deactivateCustomer}
        children={
          <DeactivateCustomer closeDetailsModal={toggleDeactivateCustomer} />
        }
      />

      <ModalPopup
        isOpen={activateCustomer}
        children={
          <ActivateCustomer closeDetailsModal={toggleActivateCustomer} />
        }
      />

      <ModalPopup
        isOpen={archiveCustomer}
        children={<ArchiveCustomer closeDetailsModal={toggleArchiveCustomer} />}
      />
    </div>
  );
};

export { OystrSmartCustomers };
