import { DashboardHeader, Sidebar } from "../molecules";
import React, { useState } from "react";

import { Outlet } from "react-router-dom";

const DashboardLayout = () => {
  const [pageHeader, setPageHeader] = useState("");
  const [ifPadding, UsePadding] = useState(false);
  const [ifButton, setIfButton] = useState(false);
  const [searchBar, setSearchBar] = useState(false);

  // const [openModal, SetOpenModal] = useState(false);
  // the toggle for sidebar
  const [sidebar, setSidebar] = useState(false);

  return (
    <div className="">
      <div
        className="block md:flex md:justify-between h-screen"
        // style={{ backgroundColor: "#e8ebee" }}
      >
        <div
          className={`grid grid-cols-10 md:block fixed transition-all delay-75 duration-500 ${
            sidebar ? "left-0" : "-left-[1000px]"
          }  w-full md:w-[17%] h-full top-0 md:left-0 z-30 overflow-auto `}
        >
          <Sidebar
            className="col-span-8 xs:col-span-7 bg-sidebarPink rounded-br-3xl rounded-tr-3xl md:rounded-br-none md:rounded-tr-none h-full"
            setSidebar={setSidebar}
          />
          {/* overlay */}
          <div
            onClick={() => setSidebar(false)}
            className={`${sidebar ? "opacity-0" : "opacity-100"} ${
              sidebar ? "visible" : "invisible"
            } transition-all delay-1000 active:delay-75 md:hidden col-span-2 xs:col-span-3 items-center z-20 bg-black opacity-50`}
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              className="h-6 w-6 text-white m-6"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
              strokeWidth="2"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                d="M6 18L18 6M6 6l12 12"
              />
            </svg>
          </div>
        </div>

        <div className="w-full md:w-[80%] justify-end lg:ml-[22%] xl:ml-[18%]">
          <div className="">
            <DashboardHeader
              setSidebar={setSidebar}
              header={pageHeader}
              ifButton={ifButton}
              searchBar={searchBar}
            />
          </div>

          <div
            className="h-full"
            // style={{ padding: ifPadding ? "20px" : "" }}
          >
            {/* {children} */}
            <Outlet
              context={{
                pageHeader,
                setPageHeader,
                UsePadding,
                setIfButton,
                setSearchBar,
              }}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export { DashboardLayout };
