import React, { useState } from "react";
import { useDispatch } from "react-redux";

import { ModalPopup } from "../../components/ModalPopup.js";
import loginIcon from "../../assets/icons/login.svg";
import archiveIcon from "../../assets/icons/archive.svg";
import activate from "../../assets/icons/activate.svg";
import deactivate from "../../assets/icons/deactivate.svg";
import { ArchiveCustomer } from "./ArchiveCustomer.js";
import { ActivateCustomer } from "./ActivateCustomer.js";
import { Text } from "../../components";
import leftArrow from "../../assets/icons/leftArrow.svg";
import { DeactivateCustomer } from "./DeactivateCustomer.js";
import { adminCustomerLoginAsync } from "../../slices/admin.js";

const MobileDataProvidersSinglePage = ({ singleCustomer, switchView }) => {
  const dispatch = useDispatch();
  const liveURL = process.env.REACT_APP_LIVE_LINK;

  const [deactivateCustomer, setDeactivateCustomer] = useState(false);
  const [activateCustomer, setActivateCustomer] = useState(false);
  const [archiveCustomer, setArchiveCustomer] = useState(false);
  const [singleItem, setSingleItem] = useState(false);

  const toggleDeactivateCustomer = () => {
    setDeactivateCustomer(!deactivateCustomer);
    setSingleItem(singleCustomer);
  };

  const toggleArchiveCustomer = () => {
    setArchiveCustomer(!archiveCustomer);
  };

  const toggleActivateCustomer = () => {
    setActivateCustomer(!activateCustomer);
    setSingleItem(singleCustomer);
  };

  const loginCustomerFnc = async () => {
    const data = await dispatch(adminCustomerLoginAsync(singleCustomer?.id));

    const access_token = data?.payload?.data?.access_token;

    if (data?.payload?.status === true) {
      window.open(`${liveURL}/sign-in?token=${access_token}`, "_blank");
    }
  };

  return (
    <div className="px-6 mt-6 md:hidden">
      <div className="mt-11">
        <div className="mb-12">
          <img src={leftArrow} alt="leftArrow" onClick={() => switchView()} />
        </div>

        <div className="flex justify-between border-b-2 border-dashed items-center pb-4">
          <Text type="h7">Name</Text>
          <Text type="body4">{singleCustomer?.name}</Text>
        </div>

        <div className="flex justify-between border-b-2 border-dashed items-center pb-4 mt-6">
          <Text type="h7">Email</Text>
          <Text type="body4">{singleCustomer?.email}</Text>
        </div>

        <div className="flex justify-between border-b-2 border-dashed items-center pb-4 mt-6">
          <Text type="h7">Phone Number</Text>
          <Text type="body4">{singleCustomer?.phone}</Text>
        </div>

        <div className="flex justify-between border-b-2 border-dashed items-center pb-4 mt-6">
          <Text type="h7">Date Onboarded</Text>
          <Text type="body4">
            {new Date(singleCustomer?.created_at)
              ?.toISOString()
              .substring(0, 10)}
          </Text>
        </div>

        <div className="flex justify-between border-b-2 border-dashed items-center pb-4 mt-6">
          <Text type="h7">Status</Text>
          <Text
            className={`font-semibold text-xs px-6 py-2 rounded-2xl${
              singleCustomer?.live_enabled
                ? " text-greenTwo bg-greenOne"
                : " text-redTwo bg-redOne"
            }`}
          >
            {singleCustomer?.live_enabled ? "Active" : "Inactive"}
          </Text>
        </div>

        <div className="flex justify-between items-center mt-8">
          <div onClick={loginCustomerFnc}>
            <img src={loginIcon} alt="login" />
            <Text type="body5" className="pt-1 flex justify-center">
              Login
            </Text>
          </div>

          <div className="">
            {singleCustomer?.live_enabled ? (
              <div onClick={toggleDeactivateCustomer}>
                <img src={deactivate} alt="deactivate" />
                <Text type="body5" className="pt-1 flex justify-center">
                  Deactivate
                </Text>{" "}
              </div>
            ) : (
              <div onClick={toggleActivateCustomer}>
                <img src={activate} alt="activate" />
                <Text type="body5" className="pt-1 flex justify-center">
                  Activate
                </Text>{" "}
              </div>
            )}
          </div>

          <div onClick={toggleDeactivateCustomer}>
            <img src={archiveIcon} alt="archive" />
            <Text type="body5" className="pt-1 flex justify-center">
              Archive
            </Text>
          </div>
        </div>
      </div>

      <ModalPopup
        isOpen={deactivateCustomer}
        children={
          <DeactivateCustomer
            closeDetailsModal={toggleDeactivateCustomer}
            singleCustomer={singleCustomer}
          />
        }
      />

      <ModalPopup
        isOpen={activateCustomer}
        children={
          <ActivateCustomer
            closeDetailsModal={toggleActivateCustomer}
            singleCustomer={singleCustomer}
          />
        }
      />

      <ModalPopup
        isOpen={archiveCustomer}
        children={<ArchiveCustomer closeDetailsModal={toggleArchiveCustomer} />}
      />
    </div>
  );
};

export default MobileDataProvidersSinglePage;
