import React from "react";
import { Link } from "react-router-dom";
import { SectionWrapper } from ".";
import Logo from "../assets/images/logo.png";
import { Text } from "../components";

const AuthLayout = ({
  children,
  ifText = false,
  title,
  textTitle,
  subText,
  className,
}) => {
  return (
    <>
      <SectionWrapper className={`${className}`}>
        <div className="responsive-container">
          <div className="flex items-center justify-between mt-8 mb-10">
            <Link to="/">
              <img
                src={Logo}
                alt="companyLogo"
                className="w-[51px] md:w-[70px] lg:w-[150px] 2xl:w-[199px]"
              />
            </Link>
          </div>

          <div className="w-full md:flex md:items-center justify-between">
            {ifText && (
              <div className="w-full text-center md:text-left mt-11 md:mt-0 md:w-[42%]">
                <p className="font-bold leading-relaxed text-lg md:text-[38px] 2xl:text-5xl lg:leading-[70px] 2xl:leading-[80px]">
                  {title}
                </p>
              </div>
            )}

            <div className="w-full mt-10 md:mt-0 md:w-[40%] h-full md:bg-white shadow-none bgShadow">
              <div className="text-center">
                <Text type="h3" className="pb-6 pt-14">
                  {textTitle}
                </Text>
                <Text type="body2" className="pb-[30px]">
                  {subText}
                </Text>
              </div>
              {children}
            </div>
          </div>
        </div>
      </SectionWrapper>
    </>
  );
};

export { AuthLayout };
