import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { toast } from "react-toastify";

import close from "../../assets/icons/close.svg";
import question from "../../assets/icons/question.svg";
import { Button, Text } from "../../components";
import { toggleCustomersLiveAsync } from "../../slices/customers";

const ActivateCustomer = ({ closeDetailsModal, singleCustomer }) => {
  const dispatch = useDispatch();
  const { toggleCustomerLiveLoading } = useSelector((state) => state.customers);

  const toggleLive = async () => {
    await dispatch(toggleCustomersLiveAsync(singleCustomer?.id))
      .unwrap()
      .then((res) => {
        toast(res?.message);
        closeDetailsModal();
        window.location.reload();
      })
      .catch((err) => {
        toast.error(err?.message);
        console.log(err);
        closeDetailsModal();
        window.location.reload();
      });
  };

  return (
    <div className="px-10">
      <div className="flex justify-end">
        <img
          src={close}
          alt="close"
          className="cursor-pointer mt-8"
          onClick={closeDetailsModal}
        />
      </div>
      <div className="flex justify-center mt-10 mb-6">
        <img src={question} alt="question mark" />
      </div>

      <section>
        <Text type="h7" className="text-center">
          Are you sure you want to activate live API for this customer
        </Text>

        <div className="mt-20 flex justify-between items-center">
          <Button
            type="tertiary"
            buttonText="No"
            onClick={closeDetailsModal}
            className="mr-12"
          />
          <Button
            type="primary"
            buttonText="Yes"
            onClick={toggleLive}
            isLoading={toggleCustomerLiveLoading}
          />
        </div>
      </section>
    </div>
  );
};

export { ActivateCustomer };
