import React, { useEffect, useState } from "react";
import { useOutletContext } from "react-router-dom";
import { useDispatch } from "react-redux";

import dashboardBgImage from "../assets/images/dashboardBg.svg";
import { Filter, Text } from "../components";
import { DashboardCard, DashboardCardTwo } from "../molecules";
import { dashboardStatsAsync } from "../slices/dashboard.js";
import wallet from "../assets/icons/wallet.svg";
import mark from "../assets/icons/mark.svg";

const Dashboard = () => {
  const { setPageHeader } = useOutletContext();
  useEffect(() => {
    setPageHeader(`General Overview`);
  }, []);

  const dispatch = useDispatch();

  const [dashboardData, setDashboardData] = useState([]);

  const getData = async () => {
    const data = await dispatch(dashboardStatsAsync());
    setDashboardData(data?.payload?.data);
  };

  useEffect(() => {
    getData();
  }, []);

  return (
    <>
      <div className="pt-8 md:pt-0 pr-4 md:pr-16">
        <div className="hidden md:block md:my-8">
          <Filter />
        </div>
      </div>

      <div
        className="w-full overflow-x-scroll md:overflow-x-hidden bg-contain bg-no-repeat pl-6 lg:pr-0 2xl:px-6 py-8 md:py-10 flex items-center mb-12 md:mb-16"
        style={{
          backgroundImage: `url(${dashboardBgImage})`,
          backgroundSize: "cover",
        }}
      >
        <DashboardCard
          title="Total Data Givers"
          totalAmount={
            dashboardData?.oystr_transactions?.total_data_givers || 0
          }
          ifNaira={false}
        />
        <DashboardCard
          title="Total Data Users"
          totalAmount={dashboardData?.oystr_transactions?.total_data_users || 0}
          ifNaira={false}
        />
        <DashboardCard
          title="Total Amount Settled"
          totalAmount={
            dashboardData?.oystr_transactions?.total_amount_settled || 0
          }
          ifInfo={false}
        />
        <DashboardCard
          title="Total Amount Generated"
          totalAmount={
            dashboardData?.oystr_transactions?.total_amount_generated || 0
          }
          ifInfo={false}
        />
      </div>

      <div>
        <Text type="h7" className="ml-8 pb-8">
          Quick Info
        </Text>

        <div className="bg-lightBlue pr-8 py-8 md:py-10 flex items-center pl-6 md:pl-8 overflow-x-scroll md:overflow-x-hidden w-full">
          <DashboardCardTwo
            icon={mark}
            number={dashboardData?.oystr_transactions?.total_data_called || 0}
            title="Completed Data called"
            bgColor="#201CFF"
          />

          <DashboardCardTwo
            icon={mark}
            number={dashboardData?.api_logs?.total_api_calls || 0}
            title="Total API Called"
            bgColor="#33CC33"
          />

          <DashboardCardTwo
            icon={wallet}
            number={dashboardData?.oystr_transactions?.total_live_enabled || 0}
            title="Total live enabled users"
            bgColor="#201CFF"
          />

          <DashboardCardTwo
            icon={wallet}
            number={`â‚¦${
              dashboardData?.oystr_transactions?.total_payment_received || 0
            }`}
            title="Total payment received"
            bgColor="#33CC33"
          />
        </div>
      </div>

      {/* <section className="mt-10">
        <Text type="h7" className="ml-8 pb-8">
          Oystr smart
        </Text>
      </section>

      <div
        className="bg-contain bg-no-repeat lg:pl-6 lg:pr-0 2xl:px-6 py-10 flex items-center mb-16"
        style={{
          backgroundImage: `url(${dashboardBgImage})`,
          backgroundSize: "cover",
        }}
      >
        <DashboardCard
          title="Total Customers customers"
          totalAmount="80"
          ifNaira={false}
        />
        <DashboardCard title="Total Agents" totalAmount="80" ifNaira={false} />
        <DashboardCard
          title="Total Transactions"
          totalAmount="80"
          ifInfo={false}
        />
        <DashboardCard
          title="Total Disburements"
          totalAmount="80"
          ifInfo={false}
        />
      </div> */}
    </>
  );
};

export { Dashboard };
