import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { toast } from "react-toastify";

import close from "../../assets/icons/close.svg";
import question from "../../assets/icons/question.svg";
import { Button, Text } from "../../components";
import { toggleAdminStatusAsync } from "../../slices/admin";

const ActivateAdmin = ({ closeDetailsModal, singleAdmin }) => {
  const dispatch = useDispatch();
  const { toggleStatusLoading } = useSelector((state) => state.admin);

  const activateAdmin = async () => {
    const variables = {
      admin_id: singleAdmin?.id.toString(),
      active: true,
      role: singleAdmin?.role,
      name: singleAdmin?.name,
      email: singleAdmin?.email,
    };

    await dispatch(toggleAdminStatusAsync(variables))
      .unwrap()
      .then((res) => {
        toast(res?.message);
        closeDetailsModal();
        window.location.reload();
      })
      .catch((err) => {
        toast.error(err?.message[0]);
        closeDetailsModal();
        window.location.reload();
      });
  };

  return (
    <div className="px-10">
      <div className="flex justify-end">
        <img
          src={close}
          alt="close"
          className="cursor-pointer mt-8"
          onClick={closeDetailsModal}
        />
      </div>
      <div className="flex justify-center mt-10 mb-6">
        <img src={question} alt="question mark" />
      </div>

      <section>
        <Text type="h7" className="text-center">
          Are you sure you want to Activate this Admin
        </Text>

        <div className="mt-20 flex justify-between items-center">
          <Button
            type="tertiary"
            buttonText="No"
            onClick={closeDetailsModal}
            className="mr-12"
          />
          <Button
            type="primary"
            buttonText="Yes"
            onClick={activateAdmin}
            isLoading={toggleStatusLoading}
          />
        </div>
      </section>
    </div>
  );
};

export { ActivateAdmin };
