const FluentSelect = ({
  options,
  onValueChange,
  selectedValue,
  className,
  labelName,
  label,
  register,
  error,
  ...rest
}) => {
  return (
    <div className="mb-6 otherSelect">
      <label className="text-xs md:text-base font-normal uppercase mb-3">
        {label}
      </label>

      <select
        {...register}
        className={`w-full rounded-2xl bg-blueTwo/[0.2] py-4 ${className}`}
        {...rest}
      >
        <option value="" selected disabled>
          {labelName}
        </option>
        {options?.map(({ value, label }) => (
          <option
            key={value}
            value={value}
            className="pb-2"
            style={{ backgroundColor: "#EEF5FF" }}
          >
            {label}
          </option>
        ))}
      </select>

      <span>
        <p className="text-red-500 text-sm">{error}</p>
      </span>
    </div>
  );
};

export { FluentSelect };
