import React from "react";

import { SearchBar, Text } from "../../components";
import dropdown from "../../assets/icons/dropdown.svg";
import { Link } from "react-router-dom";

function MobileDataConsumers({ singleCustomer, data, switchView }) {
  return (
    <div className="mx-4">
      <SearchBar placeholder="Search for Customers" />

      {data.map((item, index) => (
        <div
          key={index}
          className="mb-6 bg-mobileTableGray px-6 py-4 cursor-pointer w-full"
        >
          <div className="border-b w-full flex justify-between">
            <Link to={`/customers/${item?.id}`}>
              <Text type="h7">Name (Business Name)</Text>
              <Text type="body4" className="pt-2 pb-4">
                {item?.business?.props?.children}
              </Text>
            </Link>

            <Text
              className={`font-semibold text-xs px-6 py-2 rounded-2xl mt-4${
                item?.status?.props?.children?.props?.children === "Active"
                  ? " text-greenTwo bg-greenOne"
                  : " text-redTwo bg-redOne"
              }`}
            >
              {item?.status?.props?.children?.props?.children === "Active"
                ? "Active"
                : "Inactive"}
            </Text>
          </div>

          <div className="mt-4 w-full flex justify-between">
            <div className="">
              <Text type="h7">Email</Text>
              <Text type="body4" className="pt-2 pb-4">
                {item?.emailAddress?.props?.children}
              </Text>
            </div>

            <div
              className="flex items-center border rounded-[10px]  px-4 cursor-pointer"
              onClick={() => switchView("single", index)}
            >
              <Text type="body4" className="pr-2">
                See More
              </Text>
              <img src={dropdown} alt="dropdown" />
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}

export default MobileDataConsumers;
