const Input = ({
  placeholder,
  className,
  type,
  name,
  id,
  error,
  label,
  bgColor = "bg-s",
  readOnly,
  labelClassName,
  normalLabelCase,
  register,
  ...inputProps
}) => {
  return (
    <div className="mb-6">
      <label htmlFor={name} className="text-xs md:text-base font-normal">
        {label}
      </label>
      <input
        type={type}
        name={name}
        id={id}
        {...register}
        placeholder={placeholder}
        {...inputProps}
        className="w-full border rounded-2xl bg-blueTwo/[0.2] focus:outline-none font-medium text-[15px]"
        style={{
          border: error ? "1px solid red" : "",
          backgroundColor: readOnly ? "#ACB5BD" : "",
        }}
        readOnly={readOnly}
      />
      <span>
        <p className="text-red-500 text-xs"> {error}</p>
      </span>
    </div>
  );
};

export { Input };

