import UrlRoute from "../helpers/UrlRouter.js";
import { api } from "./api";

const getAllDataConsumers = async (pageNumber) => {
  const response = await api.get(
    `${UrlRoute.getAllDataConsumers}&page=${pageNumber}`
  );
  return response.data;
};

const getAllDataProviders = async (pageNumber) => {
  const response = await api.get(
    `${UrlRoute.getAllDataProviders}&page=${pageNumber}`
  );
  return response.data;
};

const toggleLiveConsumers = async (values) => {
  const response = await api.post(
    `${UrlRoute.toggleLiveCustomer}/${values}/toggle-live`
  );
  return response.data;
};

const getSingleCustomer = async (values) => {
  const response = await api.get(`${UrlRoute.getSingleCustomer}/${values}`);
  return response.data;
};

const deleteCustomer = async (values) => {
  const response = await api.delete(`${UrlRoute.getSingleCustomer}/${values}`);
  return response.data;
};

const restoreCustomer = async (values) => {
  const response = await api.post(
    `${UrlRoute.getSingleCustomer}/${values}/restore`
  );
  return response.data;
};

const adminService = {
  getAllDataConsumers,
  toggleLiveConsumers,
  getAllDataProviders,
  getSingleCustomer,
  deleteCustomer,
  restoreCustomer,
};

export default adminService;
