import React, { useState } from "react";

import { ModalPopup } from "../../components/ModalPopup.js";
import { DeleteAdmin } from "./DeleteAdmin.js";
import { ActivateAdmin } from "./ActivateAdmin.js";
import { EditAdmin } from "./EditAdmin.js";
import { DeactivateAdmin } from "./DeactivateAdmin.js";
import deleteIcon from "../../assets/icons/delete.svg";
import activate from "../../assets/icons/activate.svg";
import deactivate from "../../assets/icons/deactivate.svg";
import edit from "../../assets/icons/edit.svg";
import { Text } from "../../components";
import leftArrow from "../../assets/icons/leftArrow.svg";

const MobileAdminSinglePage = ({ singleAdmin, switchView }) => {
  const [deleteAdmin, setDeleteAdmin] = useState(false);
  const [activateAdmin, setActivateAdmin] = useState(false);
  const [singleItem, setSingleItem] = useState(false);
  const [editAdmin, setEditAdmin] = useState(false);
  const [deactivateAdmin, setDeactivateAdmin] = useState(false);

  const toggleDeleteAdmin = () => {
    setDeleteAdmin(!deleteAdmin);
    setSingleItem(singleAdmin);
  };

  const toggleActivateAdmin = () => {
    setActivateAdmin(!activateAdmin);
    setSingleItem(singleAdmin);
  };

  const toggleEditAdmin = () => {
    setEditAdmin(!editAdmin);
    setSingleItem(singleAdmin);
  };

  const toggleDeactivateAdmin = () => {
    setDeactivateAdmin(!deactivateAdmin);
    setSingleItem(singleAdmin);
  };
  //
  return (
    <div className="px-6 mt-6">
      <div className="mt-11">
        <div className="mb-12">
          <img src={leftArrow} alt="leftArrow" onClick={() => switchView()} />
        </div>

        <div className="flex justify-between border-b-2 border-dashed items-center pb-4">
          <Text type="h7">Name</Text>
          <Text type="body4">{singleAdmin?.name}</Text>
        </div>

        <div className="flex justify-between border-b-2 border-dashed items-center pb-4 mt-6">
          <Text type="h7">Email</Text>
          <Text type="body4">{singleAdmin?.email}</Text>
        </div>

        <div className="flex justify-between border-b-2 border-dashed items-center pb-4 mt-6">
          <Text type="h7">Status</Text>
          <Text
            className={`font-semibold text-xs px-6 py-2 rounded-2xl${
              singleAdmin?.active === 1
                ? " text-greenTwo bg-greenOne"
                : " text-redTwo bg-redOne"
            }`}
          >
            {singleAdmin?.active === 1 ? "Active" : "Inactive"}
          </Text>
        </div>

        <div className="flex justify-between border-b-2 border-dashed items-center pb-4 mt-6">
          <Text type="h7">Role</Text>
          <Text type="body4">{singleAdmin?.role}</Text>
        </div>

        <div className="flex justify-between items-center mt-8">
          <div onClick={toggleEditAdmin}>
            <img src={edit} alt="edit" />
            <Text type="body5" className="pt-1 flex justify-center">
              Edit
            </Text>
          </div>

          <div className="">
            {singleAdmin?.active === 1 ? (
              <div onClick={toggleDeactivateAdmin}>
                <img src={deactivate} alt="deactivate" />
                <Text type="body5" className="pt-1 flex justify-center">
                  Deactivate
                </Text>{" "}
              </div>
            ) : (
              <div onClick={toggleActivateAdmin}>
                <img src={activate} alt="activate" />
                <Text type="body5" className="pt-1 flex justify-center">
                  Activate
                </Text>{" "}
              </div>
            )}
          </div>

          <div onClick={toggleDeleteAdmin}>
            <img src={deleteIcon} alt="deactivate" />
            <Text type="body5" className="pt-1 flex justify-center">
              Delete
            </Text>
          </div>
        </div>
      </div>

      <ModalPopup
        isOpen={deleteAdmin}
        children={
          <DeleteAdmin
            closeDetailsModal={toggleDeleteAdmin}
            singleAdmin={singleAdmin}
          />
        }
      />

      <ModalPopup
        isOpen={activateAdmin}
        children={
          <ActivateAdmin
            closeDetailsModal={toggleActivateAdmin}
            singleAdmin={singleAdmin}
          />
        }
      />

      <ModalPopup
        isOpen={editAdmin}
        modalHeight="500px"
        children={
          <EditAdmin
            closeDetailsModal={toggleEditAdmin}
            singleAdmin={singleAdmin}
          />
        }
      />

      <ModalPopup
        isOpen={deactivateAdmin}
        children={
          <DeactivateAdmin
            closeDetailsModal={toggleDeactivateAdmin}
            singleAdmin={singleAdmin}
          />
        }
      />
    </div>
  );
};

export default MobileAdminSinglePage;
