import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";

import { getSingleAdminProfileAsync } from "../../slices/settings";
import { ModalPopup, Button, Text } from "../../components";
import { Loader, SettingsInput } from "../../atoms";
import { ResetPassword } from "./ResetPassword";
import { useOutletContext } from "react-router-dom";

const Settings = () => {
  const dispatch = useDispatch();
  const { getSingleAdminLoading, response } = useSelector(
    (state) => state.settings
  );

  const [resetPassword, setResetPassword] = useState(false);

  const toggleResetPassword = () => {
    setResetPassword(!resetPassword);
  };

  const { setPageHeader } = useOutletContext();
  useEffect(() => {
    setPageHeader(`Settings`);
  }, []);

  const formOptions = { resolver: yupResolver() };

  const { register, handleSubmit, formState, reset } = useForm({
    formOptions,
    defaultValues: {
      name: response?.name,
      email: response?.email,
      phone: response?.phone,
      role: response?.role,
    },
  });

  const { errors } = formState;

  useEffect(() => {
    dispatch(getSingleAdminProfileAsync());
  }, []);

  useEffect(() => {
    const defaultValues = {
      name: response?.name,
      email: response?.email,
      phone: response?.phone,
      role: response?.role,
    };
    reset(defaultValues);
  }, [response, reset]);

  const submitForm = (values) => {
    console.log(values);
  };

  return (
    // <p>testing</p>
    <div className="m-auto w-[90%] md:w-[70%] mt-8 md:mt-20 rounded-2xl border border-black-/[.30] ">
      <Text
        type="h6"
        className="border-b border-black-/[.20] py-4 md:py-8 pl-12"
      >
        Profile
      </Text>

      <>
        {getSingleAdminLoading ? (
          <Loader />
        ) : (
          <form onSubmit={handleSubmit(submitForm)} className="mt-10 w-full">
            <div className="px-6 md:px-16 justify-around">
              <section className="">
                <div className="grid grid-cols-2 my-6 md:my-10">
                  <div className="mb-2 md:mb-0 col-span-2 md:col-span-1">
                    <Text type="h10">Full Name</Text>
                  </div>
                  <div className="col-span-2 md:col-span-1">
                    {/* <SettingsInput
                    name="name"
                    register={register("name")}
                    error={errors?.name?.message}
                  /> */}
                    <SettingsInput
                      name="name"
                      register={register("name")}
                      error={errors?.name?.message}
                    />
                  </div>
                </div>

                <div className="grid grid-cols-2 my-6 md:my-10">
                  <div className="mb-2 md:mb-0 col-span-2 md:col-span-1">
                    <Text type="h10">Email</Text>
                  </div>
                  <div className="col-span-2 md:col-span-1">
                    <SettingsInput
                      name="email"
                      readOnly
                      register={register("email")}
                      error={errors?.email?.message}
                    />
                  </div>
                </div>

                <div className="grid grid-cols-2 my-6 md:my-10">
                  <div className="mb-2 md:mb-0 col-span-2 md:col-span-1">
                    <Text type="h10"> Support Phone</Text>
                  </div>
                  <div className="col-span-2 md:col-span-1">
                    <SettingsInput
                      name="phone"
                      register={register("phone")}
                      error={errors?.phone?.message}
                    />
                  </div>
                </div>

                <div className="grid grid-cols-2 my-6 md:my-10">
                  <div className="mb-2 md:mb-0 col-span-2 md:col-span-1">
                    <Text type="h10"> Role</Text>
                  </div>
                  <div className="col-span-2 md:col-span-1">
                    <SettingsInput
                      name="role"
                      readOnly
                      register={register("role")}
                      error={errors?.role?.message}
                    />
                  </div>
                </div>

                <div className="grid grid-cols-2 my-6 md:my-10">
                  <div className="mb-2 md:mb-0 col-span-2 md:col-span-1">
                    <Text type="h10"> Reset Password</Text>
                  </div>
                  <div className="col-span-2 md:col-span-1">
                    <div
                      className="border border-black-/[.20] rounded-md p-3 md:p-4 text-sm font-semibold text-center focus:outline-none disabled:opacity-50 cursor-pointer"
                      onClick={toggleResetPassword}
                    >
                      <p>Reset Password</p>
                    </div>
                  </div>
                </div>
              </section>
            </div>

            <div className="flex justify-between px-12 border-t py-6 md:py-10 border-black-/[.20]">
              <div className="mt-2 md:mt-0 flex justify-center md:block md:w-1/4">
                <Button type="tertiary" buttonText="Edit" />
              </div>
              <div className="hidden md:block md:w-1/4">
                <Button buttonText="Save changes" />
              </div>
            </div>
          </form>
        )}
      </>

      <ModalPopup
        children={<ResetPassword closeDetailsModal={toggleResetPassword} />}
        isOpen={resetPassword}
      />
    </div>
  );
};

export { Settings };
