import React, { useCallback, useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";

import { getAllDataProvidersAsync } from "../../slices/customers";
import { Table } from "../../components/Table.js";
import { Text } from "../../components/Text.js";
import deactivate from "../../assets/icons/deactivate.svg";
import activate from "../../assets/icons/activate.svg";
import loginIcon from "../../assets/icons/login.svg";
import archive from "../../assets/icons/archive.svg";
import { DeactivateCustomer } from "./DeactivateCustomer.js";
import { ModalPopup } from "../../components/ModalPopup.js";
import { ArchiveCustomer } from "./ArchiveCustomer.js";
import { ActivateCustomer } from "./ActivateCustomer.js";
import { Link, useOutletContext } from "react-router-dom";
import MobileDataProviders from "./MobileDataProviders";
import MobileDataProvidersSinglePage from "./MobileDataProvidersSinglePage";
import { Loader } from "../../atoms/Loader";
import { RestoreCustomer } from "./RestoreCustomer";
import { adminCustomerLoginAsync } from "../../slices/admin";

const liveURL = process.env.REACT_APP_LIVE_LINK;

const DataGivers = () => {
  const { setPageHeader } = useOutletContext();
  useEffect(() => {
    setPageHeader(`Data Givers`);
  }, []);

  const dispatch = useDispatch();
  const { getAllDataProvidersLoading } = useSelector(
    (state) => state.customers
  );

  const [deactivateCustomer, setDeactivateCustomer] = useState(false);
  const [activateCustomer, setActivateCustomer] = useState(false);
  const [archiveCustomer, setArchiveCustomer] = useState(false);
  const [restoreCustomer, setRestoreCustomer] = useState(false);
  const [dataProviders, setDataProviders] = useState([]);
  const [singleCustomer, setSingleCustomer] = useState();
  const [currentView, setCurrentView] = useState("default");
  const [totalNumberOfPages, setTotalNumberOfPages] = useState(10);
  const [pageNumber, setPageNumber] = useState("1");

  const toggleDeactivateCustomer = (item) => {
    setDeactivateCustomer(!deactivateCustomer);
    setSingleCustomer(item);
  };

  const toggleArchiveCustomer = (item) => {
    setArchiveCustomer(!archiveCustomer);
    setSingleCustomer(item);
  };

  const toggleActivateCustomer = (item) => {
    setActivateCustomer(!activateCustomer);
    setSingleCustomer(item);
  };

  const toggleRestoreCustomer = (item) => {
    setRestoreCustomer(!restoreCustomer);
    setSingleCustomer(item);
  };

  const getDataProvidersData = async () => {
    const data = await dispatch(getAllDataProvidersAsync(pageNumber));
    setDataProviders(data?.payload?.data?.customers);
    // setTotalNumberOfPages(data?.payload?.meta?.last_page);
  };

  // Invoke when user click to request another page.
  const handlePageClick = (event) => {
    setPageNumber(event.selected + 1);
  };

  const loginCustomerFnc = async (item) => {
    const data = await dispatch(adminCustomerLoginAsync(item?.id));

    const access_token = data?.payload?.data?.access_token;

    if (data?.payload?.status === true) {
      window.open(`${liveURL}/sign-in?token=${access_token}`, "_blank");
    }
  };

  useEffect(() => {
    getDataProvidersData();
  }, [pageNumber]);

  const switchView = (view = "default", index = 0) => {
    let results = {
      default() {
        setCurrentView("default");
      },
      single() {
        setCurrentView("single");
        setSingleCustomer(dataProviders[index]);
      },
    };
    results[view]();
  };

  const getData = useCallback(() => {
    const result =
      dataProviders &&
      dataProviders?.map((item, i) => {
        return {
          sn: <Text type="body2">{i + 1}</Text>,
          business: (
            <Link to={`/customers/${item?.id}`}>
              <Text type="body2" className="hover:text-darkBlue">
                {item?.name}
              </Text>{" "}
            </Link>
          ),
          emailAddress: (
            <Link to={`/customers/${item?.id}`}>
              <Text type="body2" className="hover:text-darkBlue">
                {item?.email}
              </Text>
            </Link>
          ),
          phone: (
            <Link to={`/customers/${item?.id}`}>
              <Text className="hover:text-darkBlue" type="body2">
                {item?.phone}
              </Text>
            </Link>
          ),
          date: (
            <Link to={`/customers/${item?.id}`}>
              <Text className="hover:text-darkBlue" type="body2">
                {new Date(item?.created_at)?.toISOString().substring(0, 10)}
              </Text>
            </Link>
          ),
          status: (
            <p
              className={`text-sm w-max py-1 px-4 border rounded-xl ${
                item?.live_enabled === true
                  ? "bg-greenOne text-greenTwo"
                  : "bg-redOne text-redTwo"
              }`}
            >
              <Text
                className={`font-semibold text-xs  ${
                  item?.live_enabled === true
                    ? " text-greenTwo"
                    : " text-redTwo"
                }`}
              >
                {item?.live_enabled === true ? "Active" : "Inactive"}
              </Text>
            </p>
          ),
          action: (
            <div className="flex items-center">
              <div
                className="tooltip cursor-pointer"
                onClick={() => loginCustomerFnc(item)}
              >
                <span className="tooltiptext">Login</span>
                <img src={loginIcon} alt="loginIcon" className="w-[43px]" />
              </div>

              {item?.live_enabled === false && (
                <div
                  className="tooltip cursor-pointer"
                  onClick={() => toggleActivateCustomer(item)}
                >
                  <span className="tooltiptext">Activate</span>
                  <img src={activate} alt="deactivate" className="w-[43px]" />
                </div>
              )}

              {item?.live_enabled === true && (
                <div
                  className="tooltip cursor-pointer"
                  onClick={() => toggleDeactivateCustomer(item)}
                >
                  <span className="tooltiptext">Deactivate</span>
                  <img src={deactivate} alt="deactivate" className="w-[43px]" />
                </div>
              )}

              {item?.deleted_at === null ? (
                <div
                  className="tooltip cursor-pointer"
                  onClick={() => toggleArchiveCustomer(item)}
                >
                  <span className="tooltiptext">Archive</span>
                  <img src={archive} alt="archive" className="w-[43px]" />
                </div>
              ) : (
                <div
                  className="tooltip cursor-pointer"
                  onClick={() => toggleRestoreCustomer(item)}
                >
                  <span className="tooltiptext">Restore</span>
                  <img src={archive} alt="restore" className="w-[43px]" />
                </div>
              )}
            </div>
          ),
        };
      });
    return [...(result || [])];
  }, [dataProviders]);

  const columns = React.useMemo(
    () => [
      {
        Header: "S/N",
        accessor: "sn",
      },
      {
        Header: "Business name",
        accessor: "business",
      },
      {
        Header: "Email Address",
        accessor: "emailAddress",
      },
      {
        Header: "Phone number",
        accessor: "phone",
      },
      {
        Header: "Date onboarded",
        accessor: "date",
      },
      {
        Header: "Live Enabled",
        accessor: "status",
      },
      {
        Header: "Action",
        accessor: "action",
      },
    ],
    []
  );

  const data = React.useMemo(() => getData(), [getData]);

  return (
    <div>
      {currentView === "default" ? (
        <>
          <div className="hidden md:block">
            <Table
              data={data}
              columns={columns}
              isLoading={getAllDataProvidersLoading}
              pageCount={totalNumberOfPages}
              handlePageClick={handlePageClick}
            />
          </div>
          <div className="block md:hidden mt-10">
            {getAllDataProvidersLoading ? (
              <Loader />
            ) : (
              <MobileDataProviders
                singleCustomer={singleCustomer}
                data={data}
                switchView={switchView}
              />
            )}
          </div>
        </>
      ) : (
        <div className="">
          <MobileDataProvidersSinglePage
            singleCustomer={singleCustomer}
            switchView={switchView}
          />
        </div>
      )}

      <ModalPopup
        isOpen={deactivateCustomer}
        children={
          <DeactivateCustomer
            closeDetailsModal={toggleDeactivateCustomer}
            singleCustomer={singleCustomer}
          />
        }
      />

      <ModalPopup
        isOpen={activateCustomer}
        children={
          <ActivateCustomer
