import React, { useEffect, useState } from "react";

import { Table } from "../../components/Table";
import { Text, Button, ModalPopup } from "../../components";
import { EditRole } from "./EditRole";
import { useOutletContext } from "react-router-dom";

const Roles = () => {
  const isActive = true;

  const [editRole, setEditRole] = useState(false);

  const toggleEditRole = () => {
    setEditRole(!editRole);
  };

  const { setPageHeader } = useOutletContext();
  useEffect(() => {
    setPageHeader(`Roles`);
  }, []);

  const data = React.useMemo(
    () => [
      {
        sn: <Text type="body2">1</Text>,
        roleName: <Text type="body2">Super Admin</Text>,
        date: <Text type="body2">12/02/2002</Text>,
        status: (
          <p
            className={`text-sm w-max py-1 px-4 border rounded-xl ${
              isActive === true
                ? "bg-greenOne text-greenTwo"
                : isActive === "pending"
                ? "bg-YELLOW-_100 border-YELLOW-_300 text-yellow-600"
                : "bg-redOne text-#CA0814"
            }`}
          >
            <Text className="font-semibold text-xs">
              {isActive ? "Active" : "Inactive"}
            </Text>
          </p>
        ),
        action: (
          <div className="flex items-center">
            <Button
              buttonText="Edit"
              type="tertiary"
              className="mr-4"
              size="sm"
              onClick={toggleEditRole}
            />
            <Button buttonText="View" type="primary" size="sm" />
          </div>
        ),
      },
      {
        sn: <Text type="body2">2</Text>,
        roleName: <Text type="body2">Super Admin</Text>,
        date: <Text type="body2">12/02/2002</Text>,
        status: (
          <p
            className={`text-sm w-max py-1 px-4 border rounded-xl ${
              isActive === true
                ? "bg-greenOne text-greenTwo"
                : isActive === "pending"
                ? "bg-YELLOW-_100 border-YELLOW-_300 text-yellow-600"
                : "bg-redOne text-#CA0814"
            }`}
          >
            <Text className="font-semibold text-xs">
              {isActive ? "Active" : "Inactive"}
            </Text>
          </p>
        ),
        action: (
          <div className="flex items-center">
            <Button
              buttonText="Edit"
              type="tertiary"
              className="mr-4"
              size="sm"
              onClick={toggleEditRole}
            />
            <Button buttonText="View" type="primary" size="sm" />
          </div>
        ),
      },
      {
        sn: <Text type="body2">3</Text>,
        roleName: <Text type="body2">Super Admin</Text>,
        date: <Text type="body2">12/02/2002</Text>,
        status: (
          <p
            className={`text-sm w-max py-1 px-4 border rounded-xl ${
              isActive === true
                ? "bg-greenOne text-greenTwo"
                : isActive === "pending"
                ? "bg-YELLOW-_100 border-YELLOW-_300 text-yellow-600"
                : "bg-redOne text-#CA0814"
            }`}
          >
            <Text className="font-semibold text-xs">
              {isActive ? "Active" : "Inactive"}
            </Text>
          </p>
        ),
        action: (
          <div className="flex items-center">
            <Button
              buttonText="Edit"
              type="tertiary"
              className="mr-4"
              size="sm"
              onClick={toggleEditRole}
            />
            <Button buttonText="View" type="primary" size="sm" />
          </div>
        ),
      },
    ],
    []
  );

  const columns = React.useMemo(
    () => [
      {
        Header: "S/N",
        accessor: "sn",
      },
      {
        Header: "Role Name",
        accessor: "roleName",
      },
      {
        Header: "Last Date Edited",
        accessor: "date",
      },
      {
        Header: "Status",
        accessor: "status",
      },
      {
        Header: "Action",
        accessor: "action",
      },
    ],
    []
  );

  return (
    <div>
      <Table data={data} columns={columns} />

      <ModalPopup
        isOpen={editRole}
        modalHeight="400px"
        children={<EditRole closeDetailsModal={toggleEditRole} />}
      />
    </div>
  );
};

export { Roles };
