import React, { useEffect } from "react";
import { useOutletContext, useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";

import leftArrow from "../../assets/icons/leftArrow.svg";
import defaultImage from "../../assets/images/defaultLogo.svg";
import { getSingleCustomerAsync } from "../../slices/customers";
import { Loader } from "../../atoms/Loader";
import { Filter, Text, ViewButton } from "../../components";
import { CustomerCard } from "../../molecules";

// import { useHistory } from "react-router-dom";
// import leftArrow from "../../assets/icons/leftArrow.svg";

// const BackButton = ({ className }) => {
// 	const history = useHistory();
// 	return (
// 		<>
// 			<div className="cursor-pointer" onClick={() => history.goBack()}>
// 				<img src={leftArrow} alt="leftArrow" className={`w-8 ${className}`} />
// 			</div>
// 		</>
// 	);
// };

// export default BackButton;

const Details = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const dispatch = useDispatch();
  const { getSingleCustomerLoading, response } = useSelector(
    (state) => state.customers
  );

  const { setPageHeader } = useOutletContext();
  useEffect(() => {
    setPageHeader(`User Documentation`);
  }, []);

  useEffect(() => {
    dispatch(getSingleCustomerAsync(id));
  }, []);

  // console.log(response);

  return (
    <>
      {getSingleCustomerLoading ? (
        <Loader />
      ) : (
        <div className="bg-white">
          <div
            className="flex items-center ml-6 md:ml-14 mt-6 cursor-pointer"
            onClick={() => navigate(-1)}
          >
            <img src={leftArrow} alt="leftArrow" className="mr-4" />
            <Text type="h7">{response?.name}</Text>
          </div>

          <section className="mt-6 ml-0 md:ml-5 pb-6 bg-grayTwelve pl-6">
            <div className="pt-8 mb-10">
              <img src={defaultImage} alt="defaultImage" />
            </div>

            <div className="mb-6 mr-4">
              <Filter />
            </div>

            <div className="mb-10 bg-lightBlue pr-8 py-8 md:py-10 flex items-center pl-6 md:pl-8 overflow-x-scroll md:overflow-x-hidden w-full">
              <CustomerCard title="Total Money Paid in" totalAmount="0" />
              <CustomerCard
                title="Total Data Requests"
                totalAmount={response?.api_stats?.total_data_requests || 0}
                ifNaira={false}
              />
              <CustomerCard
                title="Total Successful Data
                Requests"
                totalAmount="0"
                ifNaira={false}
              />
              <CustomerCard
                title="Total API Calls"
                totalAmount="0"
                ifNaira={false}
              />
              <CustomerCard title="Balance" totalAmount="0" ifNaira={false} />
            </div>

            <div className="flex items-center pb-5 border-b border-black/10 pt-4">
              <Text type="h8" className="min-w-[180px] md:w-[428px]">
                Last Time API was used
              </Text>
              <Text type="body1">{response?.email}</Text>
            </div>

            <div className="flex items-center pb-5 border-b border-black/10 pt-4">
              <Text type="h8" className="min-w-[180px] md:w-[428px]">
                Registered Email Address
              </Text>
              <Text type="body1">{response?.email}</Text>
            </div>

            <div className="flex items-center pb-5 border-b border-black/10 pt-4">
              <Text type="h8" className="min-w-[180px] md:w-[428px]">
                Business Id
              </Text>
              <Text type="body1">{response?.id}</Text>
            </div>

            <div className="flex items-center pb-5 border-b border-black/10 pt-4">
              <Text type="h8" className="min-w-[180px] md:w-[428px]">
                Date Registered
              </Text>
              <Text type="body1">
                {/* {new Date(response?.created_at)?.toISOString().substring(0, 10)} */}
                {response?.created_at}
              </Text>
            </div>

            <div className="flex items-center pb-5 border-b border-black/10 pt-4">
              <Text type="h8" className="min-w-[180px] md:w-[428px]">
                Status
              </Text>
              <Text
                className={`font-semibold text-xs px-6 py-2 rounded-2xl${
                  response?.live_enabled === true
                    ? " text-greenTwo bg-greenOne"
                    : " text-redTwo bg-redOne"
                }`}
              >
                {response?.live_enabled === true ? "Active" : "Inactive"}
              </Text>
            </div>

            <div className="flex items-center pb-5 border-b border-black/10 pt-4">
              <Text type="h8" className="min-w-[180px] md:w-[428px]">
                Business registration number
              </Text>
              <Text type="body1">{response?.reg_no}</Text>
            </div>

            <div className="flex items-center pb-5 border-b border-black/10 pt-4">
              <Text type="h8" className="min-w-[180px] md:w-[428px]">
                Registered Company Address
              </Text>
              <Text type="body1">{response?.address}</Text>
            </div>

            <div className="flex items-center pb-5 border-b border-black/10 pt-4">
              <Text type="h8" className="min-w-[180px] md:w-[428px]">
                Bank Verification Number
              </Text>
              <Text type="body1">2567835674</Text>
            </div>

            {/* <div className="flex items-center pb-5 border-b border-black/10 pt-4">
              <Text type="h8" className="min-w-[180px] md:w-[428px]">
                Registered Company Account details
              </Text>
              <div className="">
                <Text type="body1">
                  Account Number: {response?.banks[0].account_number}
                </Text>
                <Text type="body1" className="pt-4">
                  Account Name: {response?.banks[0].account_name}
                </Text>
                <Text type="body1" className="pt-4">
                  Bank Name: {response?.banks[0].account_bankname}
                </Text>
              </div>
            </div> */}

            <div className="flex items-center pb-5 border-b border-black/10 pt-4">
              <Text type="h8" className="min-w-[180px] md:w-[428px]">
                Company CAC Document
              </Text>
              <ViewButton href={response?.documents?.cac_document} />
            </div>

            <div className="flex items-center pb-5 border-b border-black/10 pt-4">
              <Text type="h8" className="min-w-[180px] md:w-[428px]">
                Company’s Directors Agreements
              </Text>
              <ViewButton href={response?.documents?.board_resolution} />
            </div>

            <div className="flex items-center pb-5 border-b border-black/10 pt-4">
              <Text type="h8" className="min-w-[180px] md:w-[428px]">
                Company’s Shareholders Agreements
              </Text>
              <ViewButton href={response?.documents?.share_allotment} />
            </div>
          </section>
        </div>
      )}
    </>
  );
};

export { Details };
