import React from "react";
import { useDispatch, useSelector } from "react-redux";

import { deleteAdminAsync } from "../../slices/admin";
import close from "../../assets/icons/close.svg";
import question from "../../assets/icons/question.svg";
import { Button, Text } from "../../components";
import { toast } from "react-toastify";

const DeleteAdmin = ({ closeDetailsModal, singleAdmin }) => {
  // console.log(singleAdmin);

  const dispatch = useDispatch();
  const { deleteAdminLoading } = useSelector((state) => state.admin);

  const deleteAdmin = async () => {
    await dispatch(deleteAdminAsync(singleAdmin?.id))
      .unwrap()
      .then((res) => {
        toast(res?.message);
        closeDetailsModal();
        window.location.reload();
      })
      .catch((err) => {
        console.log(err);
        toast.error(err?.message);
        closeDetailsModal();
        window.location.reload();
      });
  };

  return (
    <div className="px-10">
      <div className="flex justify-end">
        <img
          src={close}
          alt="close"
          className="cursor-pointer mt-8"
          onClick={closeDetailsModal}
        />
      </div>
      <div className="flex justify-center mt-10 mb-6">
        <img src={question} alt="question mark" />
      </div>

      <section>
        <Text type="h7" className="text-center">
          Are you sure you want to delete this Admin
        </Text>

        <div className="mt-20 flex justify-between items-center">
          <Button
            type="tertiary"
            buttonText="No"
            onClick={closeDetailsModal}
            className="mr-12"
          />
          <Button
            type="delete"
            buttonText="Yes"
            onClick={deleteAdmin}
            isLoading={deleteAdminLoading}
          />
        </div>
      </section>
    </div>
  );
};

export { DeleteAdmin };
