const Input = ({
  placeholder,
  className,
  type,
  name,
  id,
  error,
  label,
  bgColor = "bg-s",
  readOnly,
  labelClassName,
  normalLabelCase,
  register,
  ...inputProps
}) => {
  return (
    <div className="mb-6">
      <label
        htmlFor={name}
        className={` ${
          normalLabelCase ? "" : "uppercase text-xs md:text-base font-normal"
        } mb-3 ${labelClassName}`}
      >
        {label}
      </label>
      <input
        type={type}
        name={name}
        id={id}
        {...register}
        placeholder={placeholder}
        {...inputProps}
        className="w-full border rounded-lg bg-blueTwo focus:outline-none focus:border-lighterBlue px-2 py-2 md:py-4"
        style={{
          border: error ? "1px solid red" : "",
          backgroundColor: readOnly ? "#ACB5BD" : "",
        }}
        readOnly={readOnly}
      />
      <span>
        <p className="text-red-500 text-xs"> {error}</p>
      </span>
    </div>
  );
};

export { Input };
