import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";

import { getSingleAdminProfileAsync } from "../slices/settings";
import sideArrowDown from "../assets/icons/sideArrowDown.svg";
import sideArrowUp from "../assets/icons/sideArrowUp.svg";
// import search from "../assets/icons/search.svg";
import { DesktopSearchBar, Filter, ModalPopup } from "../components";
import { Text } from "../components/Text";
import { AddAdmin } from "../pages/Admin/AddAdmin";
import { LogoutModal } from "./LogoutModal";

const DashboardHeader = (props) => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const { response } = useSelector((state) => state.settings);

  const [addAdmin, setAddAdmin] = useState(false);
  const [showDropdown, setShowDropdown] = React.useState(false);
  const [openLogoutModal, setOpenLogoutModal] = useState(false);
  const location = useLocation();

  const toggleAddAdmin = () => {
    setAddAdmin(!addAdmin);
  };

  const toggleLogoutModal = () => {
    setOpenLogoutModal(!openLogoutModal);
  };

  const toggleDropdownModal = () => {
    console.log("show on mobile");
    setShowDropdown(!showDropdown);
  };

  const goToSettingsPage = () => {
    navigate("/settings", { replace: true });
  };

  useEffect(() => {
    dispatch(getSingleAdminProfileAsync());
  }, []);

  return (
    <>
      <div className="fixed top-0 w-full px-6 py-4 md:hidden md:bg-blueOne">
        <div className="flex justify-between items-center">
          <svg
            onClick={() => props.setSidebar(true)}
            xmlns="http://www.w3.org/2000/svg"
            className="h-6 w-6 cursor-pointer"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
            strokeWidth={2}
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              d="M4 6h16M4 12h16M4 18h16"
            />
          </svg>
        </div>
      </div>
      <header
        className={`bg-headerGray md:rounded-tl-lg md:rounded-tr-lg md:pt-0 w-full flex items-center justify-between border-b-2 md:mt-6`}
      >
        <div className="flex items-center lg:w-[40%] xl:w-[25%]">
          <Text type="h3" className="hidden md:block py-4 pl-8">
            {props.header || ""}
          </Text>
          {location.pathname === "/admin" && (
            <div
              className="hidden md:flex items-center cursor-pointer ml-8 border border-grayThree py-2 px-3 rounded-lg bg-white"
              onClick={toggleAddAdmin}
            >
              <p className="text-2xl mr-4">+</p>
              <p className="text-base font-medium text-grayFour">Add Admin</p>
            </div>
          )}
        </div>

        <div className="lg:w-[60%] xl:w-[75%] justify-between py-5 pl-8 flex items-center pr-4 cursor-pointer">
          <div className="lg:w-[100%] xl:w-[60%] lg:mr-8 xl:mr-16">
            {location.pathname === "/admin" ||
              location.pathname === "/customers/data-consumers" ||
              (location.pathname === "/customers/data-givers" && (
                <DesktopSearchBar placeholder="Search for Customers" />
              ))}
          </div>

          <div
            className="w-full md:w-[40%] flex items-center"
            onClick={toggleDropdownModal}
          >
            <Text type="body2" className="pr-3">
              {response?.name}
            </Text>

            {showDropdown ? (
              <img src={sideArrowUp} alt="dropDown" />
            ) : (
              <img src={sideArrowDown} alt="dropDown" />
            )}
          </div>

          {showDropdown && (
            <div
              className={`${
                showDropdown ? "" : ""
              } z-40 rounded-md absolute bg-white border border-black/20 ml-4 md:ml-[3.6%] top-[4rem]`}
            >
              <div>
                <p
                  className="py-1 md:py-4 pl-4 pr-10 font-normal text-lg cursor-pointer hover:bg-grayThree"
                  onClick={goToSettingsPage}
                >
                  Settings
                </p>
                <p
                  className="border-t border-black/20 py-1 md:py-4 pl-4 pr-10 font-normal text-lg cursor-pointer hover:bg-grayThree"
                  onClick={toggleLogoutModal}
                  onMouseEnter={() => {
                    setShowDropdown(true);
                  }}
                >
                  Logout
                </p>
              </div>
            </div>
          )}
        </div>

        {/* <ModalPopup
          isOpen={addAdmin}
          children={<AddAdmin closeDetailsModal={toggleAddAdmin} />}
        />

        <ModalPopup
          isOpen={openLogoutModal}
          children={<LogoutModal closeDetailsModal={toggleLogoutModal} />}
        /> */}
      </header>

      <div className="md:hidden mt-8 flex items-center justify-between px-6">
        <p className="md:hidden text-lg font-medium">{props.header || ""}</p>
        {location.pathname === "/dashboard" && <Filter />}
        {location.pathname === "/admin" && (
          <div
            className="flex items-center cursor-pointer ml-4 border border-grayThree py-2 px-3 rounded-lg bg-white"
            onClick={toggleAddAdmin}
          >
            {/* <p className="text-2xl mr-4">+</p> */}
            <p className="text-base font-medium text-grayFour">Add Admin</p>
          </div>
        )}
      </div>
    </>
  );
};

export { DashboardHeader };
